// Normalizer for agent output (object with user_intent, user_location, response, and places)
// Outputs: { response, items, user_intent, user_location, source, error }

const input = $json || {};
const outputObj = input?.output || input?.raw?.output || {};

// 1. Handle stringified output (rare fallback)
let output = outputObj;
if (typeof output === "string") {
  try {
    output = JSON.parse(output);
  } catch {
    output = {};
  }
}

// 2. Pull fields directly
const user_intent    = output?.user_intent    ?? null;
const user_location  = output?.user_location  ?? null;
const response       = output?.response       ?? null;
const items          = Array.isArray(output?.places) ? output.places : [];

// 3. Fallbacks from intermediateSteps if missing
if ((!user_intent || !user_location) && Array.isArray(input?.intermediateSteps)) {
  for (const step of input.intermediateSteps) {
    const toolInput = step?.action?.toolInput;
    if (toolInput) {
      if (!user_intent && toolInput.intent) user_intent = toolInput.intent;
      if (!user_location && toolInput.location) user_location = toolInput.location;
      if (user_intent && user_location) break;
    }
  }
}

// 4. Output clean structure
return {
  response,
  items,
  user_intent,
  user_location,
  source: "format-normalizer",
  error: null
};

// Code node ‚Äî Run Once for All Items
// Input: items where each item.json has { output: { user_intent, user_location, response, places[] } }
// Output: ARRAY with a single item: { json: { text: "<prettified markdown>" } }

const items = $input.all();

// ---- helpers ----
const asArray = (v) => Array.isArray(v) ? v : (v == null ? [] : [v]);
const safe = (s) => (s ?? "").toString().trim();
const fmtPct = (n) => (typeof n === "number" && isFinite(n)) ? `${Math.round(n * 100)}%` : "‚Äî";
const fmtDate = (iso) => {
  if (!iso) return "‚Äî";
  const d = new Date(iso);
  return isNaN(d.getTime()) ? "‚Äî" : d.toISOString().slice(0, 10);
};
const fmtCoords = (c) => (c && typeof c.lat === "number" && typeof c.lng === "number")
  ? `${c.lat.toFixed(5)}, ${c.lng.toFixed(5)}`
  : "‚Äî";

function extractOutputs(allItems) {
  const outs = [];
  for (const it of allItems) {
    const j = it?.json ?? {};
    if (j.output && typeof j.output === "object") outs.push(j.output);
    // also allow the case where the object itself *is* the output
    else if (j.user_intent || j.places || j.response) outs.push(j);
    // allow nested arrays (rare)
    else if (Array.isArray(j)) {
      for (const maybe of j) {
        if (maybe?.output) outs.push(maybe.output);
      }
    }
  }
  return outs;
}

function formatPlace(p, idx) {
  const name = safe(p.name) || `Place ${idx + 1}`;
  const addr = safe(p.address);
  const cityState = [safe(p.city), safe(p.state)].filter(Boolean).join(", ");
  const country = safe(p.country);
  const locLine = [addr, [cityState, country].filter(Boolean).join(", ")].filter(Boolean).join(" ‚Äî ");
  const stars = fmtPct(p.stonewalker_stars ?? p.confidence);
  const source = safe(p.source);
  const url = safe(p.url);
  const coords = fmtCoords(p.coordinates);
  const cached = fmtDate(p.cached_at);
  const tags = Array.isArray(p.tags) && p.tags.length ? p.tags.join(", ") : "‚Äî";

  const lines = [];
  lines.push(`- **${name}**  \n  Stonewalker Stars: **${stars}**`);
  if (locLine) lines.push(`  Location: ${locLine}`);
  if (coords !== "‚Äî") lines.push(`  Coords: ${coords}`);
  if (source) lines.push(`  Source: ${source}`);
  if (url) lines.push(`  Link: ${url}`);
  if (tags !== "‚Äî") lines.push(`  Tags: ${tags}`);
  if (cached !== "‚Äî") lines.push(`  Cached: ${cached}`);
  const desc = safe(p.description);
  if (desc) lines.push(`  _${desc}_`);
  return lines.join("\n");
}

const outputs = extractOutputs(items);

if (!outputs.length) {
  return [{ json: { text: "No results to display." } }];
}

const chunks = [];

for (const out of outputs) {
  const header = `### ${safe(out.user_intent) ? `Intent: **${safe(out.user_intent)}**` : "Result"}  \n${safe(out.user_location) ? `Location: **${safe(out.user_location)}**` : ""}`.trim();
  const prose = safe(out.response);
  const places = asArray(out.places)
    .filter(p => p && typeof p === "object")
    .sort((a, b) => (b.stonewalker_stars ?? b.confidence ?? 0) - (a.stonewalker_stars ?? a.confidence ?? 0));

  chunks.push(header || "### Result");
  if (prose) chunks.push(`\n${prose}\n`);

  if (places.length) {
    chunks.push("**Top Picks**");
    for (let i = 0; i < places.length; i++) {
      chunks.push(formatPlace(places[i], i));
    }
  } else {
    chunks.push("_No specific places were returned._");
  }
}

// Return a single chat-friendly markdown blob
return [
  {
    json: {
      text: chunks.join("\n\n")
    }
  }
];
